import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Paper from '@material-ui/core/Paper';
import InputBase from '@material-ui/core/InputBase';
import IconButton from '@material-ui/core/IconButton';
import Grid from "@material-ui/core/Grid";
import { Typography } from "@material-ui/core";
import Logo from "../images/ostello_logo.png";
import CheckCircleOutlineOutlinedIcon from "@material-ui/icons/CheckCircleOutlineOutlined";

// CSS Codes
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "center",
    color: theme.palette.text.secondary,
  },
  Blog_Name: {
    fontSize: "43px",
    fontFamily: "Calibri",
    FontWeight: "Italic",
    color: "#6A6A6A",
    marginLeft: "8rem",
    marginTop: "1rem",
  },
  logo_img: {
    marginLeft: "8rem",
    width: "126px",
    bottom: "486px",
    marginTop: "8rem",
    cursor: "pointer",
    height: "56px",
  },
  home_info: {
    marginLeft: "8rem",
    fontSize: "16px",
    color: "#474747",
    fontFamily: "Calibri",
    fontStyle: "normal",
    fontWeight: "normal",
  },
  subscribe_heading: {
    color: "#2F2F2F",
    marginLeft: "8rem",
    fontStyle: "normal",
    fontSize: "12px",
    fontFamily: "Calibri",
    marginTop: "5rem",
  },
  text_susbcribe: {
    marginLeft: "8rem",
    marginTop: "0.5rem",
    width: "18rem",
    borderRadius: "5px",
    boxShadow: "0px 4px 5px rgba(0, 0, 0, 0.1)",
  },
}));

export default function FullWidthGrid() {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Grid container spacing={3}>
        <Grid item xs={8}>
          <Typography>
            <img className={classes.logo_img} src={Logo} alt="Logo"></img>
          </Typography>
          <Typography className={classes.Blog_Name}>Excellence</Typography>
          <Typography className={classes.home_info}>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Condimentum
            magna massa vitae amet purus.
            <br />
            Consequat tincidunt in blandit mattis nulla eu, turpis ut. Sem in
            diam arcu et ac vel sed. Sed nunc sit
          </Typography>
          <Typography className={classes.subscribe_heading}>
            Subscribe now, amazing contets on your way!
          </Typography>
          <Paper component="form" className={classes.text_susbcribe}>
            <InputBase
              placeholder="Jondoeostello579@gmail.com"
            />
            <IconButton
              color="primary"
              className={classes.iconButton}
              aria-label="directions"
            >
                <CheckCircleOutlineOutlinedIcon style={{marginLeft:"2rem"}}/>
            </IconButton>
          </Paper>
        </Grid>
        <Grid item xs={4}></Grid>
      </Grid>
    </div>
  );
}
