import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import Button from "@material-ui/core/Button";
import Link from "@material-ui/core/Link";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "center",
    color: theme.palette.text.secondary,
  },
}));

export default function CenteredGrid() {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Grid container spacing={3}>
        <Grid item xs={12}>
        <h4 className="text-center mb-3"><u>Create Blog</u></h4>
            <form className="form" onSubmit={handleBlogSubmit}>
                <div className="form-group">
                    <label htmlFor="title">Blog TItle</label>
                    <input type="text" placeholder="Blog Title" className="form-control" value={title} onChange={(e) => setTitle(e.target.value)} name="title" required={true}></input>
                </div>
                <div className="form-group">
                    <label htmlFor="content">Blog Content</label>
                    <textarea placeholder="Blog Content" rows="8" className="form-control" value={content} onChange={(e) => { setContent(e.target.value) }} name="content" required={true}></textarea>
                </div>
                <div className="form-group">
                    <label htmlFor="image">Blog Image</label>
                    <input type="file" accept="image/png, image/jpeg" className="form-control" name="image" onChange={(e) => { setImage(e.target.files[0]) }}></input>
                </div>
                <div className="d-flex justify-content-center align-items-center">
                    <button type="submit" className="btn btn-success">Create Blog</button>
                </div>
            </form>       
        </Grid>
      </Grid>
      <Grid container spacing={8}>
          <Grid item xs={12}>
          <TextField id="outlined-basic" label="Enter your Bog Heading" variant="outlined" />
          </Grid>
      </Grid> 
    </div>
  );
}
