  }
}));

export default function ImageAvatars() {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Avatar alt="Remy Sharp" src="/static/images/avatar/1.jpg" />
      <h6 className={classes.userProfile}>John Doe,National Institute of Fashion Technology</h6>
      <Grid>
      <h6 className={classes.location}>Bengaluru, India</h6>
      </Grid>
      
    </div>
  );
}
