import { Divider, makeStyles } from "@material-ui/core";
import React from "react";
import HomeImg from "../images/Blog-image.jpg";
import Typography from '@material-ui/core/Typography';
import Grid from "@material-ui/core/Grid";
import CardDetailsinfo from "./CardDetailsinfo";
import BlogAvatar from "./BlogAvatar";

const useStyles = makeStyles((theme) => ({
  HomeImg: {
    width: "1445px",
    height: "423px",
    left: "-103px",
  },
  home_title:{
    fontFamily:"Gotham-bold",
    fontWeight:"normal",
    fontSize:"13px",
    color:"#4E4E4E"
  }
}));

export default function FullWidthGrid() {
  const classes = useStyles();

  return (
    <div>
      <Grid container>
        <Grid items xs={12}>
          {/* <Divider/>
          <img src={HomeImg} className={classes.HomeImg} alt="HomeImg" /> */}
          <Paper className={classes.mainFeaturedPost}>
      {/* Increase the priority of the hero background image */}
      {<img style={{ display: 'none' }} src={HomeImg} alt="HomeImg" />}
      <div className={classes.overlay} />
      <Grid container>
        <Grid item md={6}>
          <div className={classes.mainFeaturedPostContent}>
            <Typography component="h1" variant="h3" color="inherit" gutterBottom>
            </Typography>
            <Typography variant="h5" color="inherit" paragraph>
              {post.description}
            </Typography>
          </div>
        </Grid>
      </Grid>
    </Paper>
        </Grid>
        <Grid item xs={4}>
          <BlogAvatar/>
        </Grid>
        <Grid item xs={6}>
            <CardDetailsinfo/>
            <h5 className={classes.home_title}>HOME</h5>
        </Grid>
        <Grid item xs={2}></Grid>
      </Grid>
    </div>
  );
}
