// BLog Home Updated
import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import InputBase from "@material-ui/core/InputBase";
import IconButton from "@material-ui/core/IconButton";
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";
import { Typography } from "@material-ui/core";
import Logo from "../images/ostello_logo.png";
import CheckCircleOutlineOutlinedIcon from "@material-ui/icons/CheckCircleOutlineOutlined";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import Logo2 from "../images/undraw_Development_re_g5hq.svg";
import Blog_card_list from "./Blog_card_list";
import Pagination_blog from "./Pagination_blog";
import InstagramIcon from "@material-ui/icons/Instagram";
import FacebookIcon from "@material-ui/icons/Facebook";
import LinkedInIcon from "@material-ui/icons/LinkedIn";

// CSS Codes
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  root1: {
    padding: "2px 4px",
    display: "flex",
    alignItems: "center",
    width: "313px",
    marginLeft: "150px",
    marginTop: "0.8rem",
    boxShadow:"0px 4px 5px rgba(0, 0, 0, 0.1)"
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "center",
    color: theme.palette.text.secondary,
  },
  Blog_Name: {
    fontSize: "42.0249px",
    fontFamily: "Calibri",
    FontWeight: "bold",
    color: "#6A6A6A",
    marginLeft: "150px",
    top: "238px",
    lineHeight:"40px",
    fontStyle:"normal"
  },
  logo_img: {
    marginLeft: "150px",
    width: "199.44px",
    bottom: "486px",
    marginTop: "6rem",
    cursor: "pointer",
    height: "64.51px",
  },
  home_info: {
    marginLeft: "150px",
    fontSize: "12px",
    color: "#474747",
    fontFamily: "Calibri",
    fontStyle: "normal",
    fontWeight: "normal",
    width:"448px",
    lineHeight:"13px"
  },
  subscribe_heading: {
    color: "#2F2F2F",
    marginLeft: "150px",
    fontStyle: "bold",
    fontSize: "12px",
    fontFamily: "Calibri",
    marginTop: "5rem",
    lineHeight:"13px"
  },
  text_susbcribe: {
    marginLeft: "8rem",
    marginTop: "0.5rem",
    width: "18rem",
    borderRadius: "5px",
    boxShadow: "0px 4px 5px rgba(0, 0, 0, 0.1)",
  },
  input: {
    marginLeft: theme.spacing(1),
    flex: 1,
    borderRadius:"9px",
    height:"55px",
  },
  iconButton: {
    padding: 10,
  },
  divider: {
    height: 28,
    margin: 4,
  },
  down_card: {
    color: "#6074E3",
    marginTop: "38px",
    cursor: "pointer",
    fontSize: "83px",
  },
  arrow_card_down: {
    color: "#6074E3",
    marginLeft: "134px",
    marginTop: "8px",
    cursor: "pointer",
    fontSize: "102px",
  },
  article_heading: {
    fontFamily: "Calibri",
    fontWeight: "normal",
    color: "#2F2F2FF",
    fontSize: "43px",
    textAlign: "center",
    letterSpacing: "0.25rem",
  },
}));

export default function FullWidthGrid() {
  const classes = useStyles();

  return (
    <div>
      <Grid
        container
        spacing={3}
        style={{ backgroundColor: "#FFF9F9", height: "734px" }}
      >
        <Grid item xs={5}>
          <Typography>
            <img className={classes.logo_img} src={Logo} alt="Logo"></img>
          </Typography>
          <Typography className={classes.Blog_Name}>Excellence</Typography>
          <Typography className={classes.home_info}>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit.  nulla eu, turpis ut. Sem in diam arcu et ac vel sed. Sed nunc sit mauris id lorem imperdiet tellus mattis.
            <br />
            Condimentum magna massa vitae amet purus. Consequat tincidunt in blandit mattis
            Consequat tincidunt in blandit mattis nulla eu, turpis ut.
          </Typography>
          <Typography className={classes.subscribe_heading}>
            Subscribe now, amazing contets on your way!
          </Typography>
          <Paper component="form" className={classes.root1}>
            <InputBase
              className={classes.input}
              placeholder="Jondoeostello579@gmail.com"
              inputProps={{ "aria-label": "subscribe for blog updates" }}
            />
            <IconButton
              color="primary"
              className={classes.iconButton}
              aria-label="directions"
            >
              <CheckCircleOutlineOutlinedIcon style={{ color: "green" }} />
            </IconButton>
          </Paper>
          <Button
            variant="contained"
            size="large"
            style={{
              width: "317px",
              backgroundColor: "#6074E3",
              color: "#fff",
              boxShadow: "0px 4px 10px rgba(46, 78, 255, 0.47)",
              marginLeft: "150px",
              marginTop: "1.2rem",
              height:"57px"
            }}
          >
            Get Started
          </Button>
        </Grid>
        <Grid item xs={6}>
          <img
            src={Logo2}
            alt="Logo2"
            style={{
              fontSize: "31px",
              width:"664px",
              height: "322px",
              cursor: "pointer",
              marginTop: "231px",
            }}
          ></img>
        </Grid>
        <Grid item xs={1}>
            <FacebookIcon
              style={{
                marginTop: "14.8rem",
                marginLeft: "2.5rem",
                color: "#959BAB",
                cursor: "pointer"
              }}
            />
            <br />
            <br />
            <InstagramIcon style={{ marginLeft: "2.5rem", color: "#959BAB",cursor: "pointer", opacity:"0.6" }} />
            <br />
            <br />
            <LinkedInIcon style={{ marginLeft: "2.5rem", color: "#959BAB",cursor: "pointer" }} />
        </Grid>
        <Grid item xs={12}>
          <ExpandMoreIcon className={classes.arrow_card_down} />
        </Grid>
      </Grid>
      <Grid item xs={12}>
        <h5 className={classes.article_heading}>ARTICLES</h5>
      </Grid>
      <Blog_card_list />
      <Pagination_blog />
    </div>
  );
}
