import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import SendIcon from "@material-ui/icons/Send";
import WallpaperIcon from "@material-ui/icons/Wallpaper";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
// import Logo from "../images/logo.png";
import Container from "@material-ui/core/Container";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "center",
    color: theme.palette.text.secondary,
  },
}));

export default function CenteredGrid() {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Container maxWidth="lg"></Container>
      <Grid container spacing={2}>
        {/* Grid for Blog Title1 */}
        <Grid item xs={2}>
          <ArrowBackIosIcon
            variant="contained"
            style={{
              color: "#6074E3",
              marginLeft: "55px",
              marginTop: "38px",
              cursor: "pointer",
              fontSize: "42px",
            }}
          />
        </Grid>
        <Grid item xs={6} alignItems="stretch">
          <h1
            className={classes.paper}
            style={{
              fontSize: "30px",
              fontFamily: "Gotham",
              fontStyle: "normal",
              color: "#6074E3",
            }}
          >
            WRITE A BLOG
          </h1>
        </Grid>
        <Grid item xs={2}>
          {/* <img src={Logo} alt="Logo" style={{ marginRight: "136px", marginTop:"26px" }}></img> */}
        </Grid>
        <Grid item xs={2}>
          <h5
            style={{
              fontFamily: "Gotham",
              fontStyle: "normal",
              fontWeight: "bold",
              color: "#6074E3",
              fontSize: "18px",
              marginTop: "42px",
            }}
          >
            Ostello
          </h5>
        </Grid>

        {/* Grid for Blog Title Name */}
        <Grid item xs={1}></Grid>
        <Grid item xs={2}></Grid>
        <Grid item xs={6}>
          <TextField
            style={{
              margin: 2,
              boxShadow: "0px 4px 12px rgba(0, 0, 0, 0.16)",
              fontFamily: "Gotham",
              borderRadius: "12px",
            }}
            fullWidth
            placeholder="Blog Tilte"
            margin="small"
            variant="outlined"
          />
        </Grid>
        <Grid item xs={3}></Grid>

        {/* Grid for Blog Story Content*/}
        <Grid item xs={1}></Grid>
        <Grid item xs={2}></Grid>
        <Grid item xs={6}>
          <TextField
            style={{
              boxShadow: "0px 4px 12px rgba(0, 0, 0, 0.16)",
              fontFamily: "Gotham",
              borderRadius: "12px",
            }}
            fullWidth
            placeholder="Tell us your story"
            variant="outlined"
            multiline
            rows={42}
            rowsMax={12}
          ></TextField>
        </Grid>
        <Grid item xs={3}></Grid>

        {/* Grid for Content Image */}
        <Grid item xs={1}></Grid>
        <Grid item xs={2}></Grid>
        <Grid item xs={4}>
          <Button
            variant="contained"
            color="default"
            style={{
              position: "absolute",
              marginTop: "6px",
            }}
          >
            Upload Content Image
            <WallpaperIcon />
          </Button>
        </Grid>
        <Grid item xs={3}></Grid>
        <Grid item xs={2}></Grid>

        {/* Grid for Cover Image */}
        <Grid item xs={1}></Grid>
        <Grid item xs={2}></Grid>
        <Grid item xs={4}>
          <Button
            variant="contained"
            color="default"
            style={{
              marginTop: "46px",
            }}
          >
            Upload Content Image
            <WallpaperIcon style={{color:"#6074E3"}} />
          </Button>
        </Grid>
        <Grid item xs={3}></Grid>
        <Grid item xs={2}></Grid>


        {/* Grid for Publish Button */}
          <Grid item md={6}></Grid>
          <Grid item xs={4}>
            <Button
              variant="contained"
              style={{
                width: "173px",
                backgroundColor: "#6074E3",
                color: "#fff",
              }}
            >
              <SendIcon />
              Publish
            </Button>
          </Grid>
          <Grid item xs={2}></Grid>
      </Grid>
    </div>
  );
}
