import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import CloudUploadIcon from "@material-ui/icons/CloudUpload";
import Button from "@material-ui/core/Button";
import PublishIcon from "@material-ui/icons/Publish";
import WallpaperIcon from '@material-ui/icons/Wallpaper';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "center",
    color: theme.palette.text.secondary,
  },
}));

export default function CenteredGrid() {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Grid container spacing={3}>
      <Grid item xs={3}>
      </Grid>
        <Grid item xs={6}>
          <h1
            className={classes.paper}
            style={{
              fontSize: "41px",
              fontFamily: "Gotham",
              fontStyle: "normal",
              fontWeight: "bold",
              color: "#646464",
            }}
          >
            W R I T E <br/>A BLOG
          </h1>
        </Grid>
      <Grid item xs={3}>
      </Grid>
        {/* Grid1 */}
        <Grid item xs={3}></Grid>
        <Grid item xs={6}>
          <TextField
            style={{ margin: 6 }}
            placeholder="Blog Title"
            fullWidth
            margin="small"
            variant="outlined"
          />
        </Grid>
        <Grid item xs={3}></Grid>
        {/* Grid2 */}
        <Grid item xs={3}></Grid>
        <Grid item xs={3}>
        </Grid>
        <Grid item xs={3}>
        </Grid>
        <Grid item xs={3}></Grid>
        {/* Grid3 */}
        <Grid item xs={3}></Grid>
        <Grid item xs={6}>
          <TextField
            fullWidth
            variant="outlined"
            placeholder="Tell us your story"
            multiline
            rows={42}
            rowsMax={12}
            size="123px"
          ></TextField>
        </Grid>
        <Grid item xs={3}></Grid>
        {/* Grid3 */}
        {/* Grid4 */}
         <Grid item xs={3}></Grid>
         <Grid item xs={3}>
         <Button
            variant="contained"
            color="default"
            style={{
              position: "absolute",
              width: "265px",
            }}
          >
            <WallpaperIcon />
            Upload Cover Image
          </Button>
         </Grid>
         <Grid item xs={3}></Grid>
         <Grid item xs={3}></Grid>
        {/* Grid5 */}
                {/* Grid4 */}
         <Grid item xs={3}></Grid>
         <Grid item xs={3}>
         <Button
            variant="contained"
            color="default"
            style={{
              position: "absolute",
              width: "265px",
            }}
          >
            <WallpaperIcon />
            Upload Content Image
          </Button>
         </Grid>
         <Grid item xs={3}></Grid>
         <Grid item xs={3}></Grid>
        {/* Grid5 */}
        <Grid item xs={3}></Grid>
        <Grid item xs={3}>
          <Button
            variant="primary"
            style={{
              position: "absolute",
              width: "173px",
              backgroundColor: "#6074E3",
              color: "#fff",
              marginTop:"48px"
            }}
          >
            <PublishIcon />
            Publish
          </Button>
        </Grid>
        <Grid item xs={6}></Grid>
      </Grid>
    </div>
  );
}
